<script>
    //翻页
    Pagination = {
        code: '',
        Extend: function (data) {
            Pagination.size = data.size;
            Pagination.page = data.page;
            Pagination.step = data.step;
        },
        Add: function (s, f) {
            for (var i = s; i < f; i++) {
                Pagination.code += '<a>' + i + '</a>';
            }
        },
        Last: function () {
            Pagination.code += '<i>...</i><a>' + Pagination.size + '</a>';
        },
        First: function () {
            Pagination.code += '<a>1</a><i>...</i>';
        },

        Click: function () {
            Pagination.page = +this.innerHTML;
            Pagination.Start();
            window.location = '/home/newslist?page=' + Pagination.page + '&t_id=4';
        },
        Prev: function () {
            var num='1';
            num--;
            if (num < 1) {
                num = 1;
            }
            Pagination.Start();
            window.location = '/home/newslist?page=' + num + '&t_id=4';
        },
        Next: function () {
            var num='1';
            num++;
            if (num > Pagination.size) {
                num = Pagination.size;
            }
            
            Pagination.Start();
            window.location = '/home/newslist?page=' + num + '&t_id=4';
        },

        Bind: function () {
            var a = Pagination.e.getElementsByTagName('a');
            for (var i = 0; i < a.length; i++) {
                a[i].onclick = Pagination.Click;
            }
            var a = Pagination.e.getElementsByTagName('a');
            for (var i = 0; i < a.length; i++) {
                if (a[i].innerHTML == '1') {
                    a[i].className = 'current';
                }
            }
        },
        Finish: function () {
            Pagination.e.innerHTML = Pagination.code;
            Pagination.code = '';
            Pagination.Bind();
        },
        Start: function () {
            if (Pagination.size < Pagination.step * 2 + 6) {
                Pagination.Add(1, Pagination.size + 1);
            }
            else if (Pagination.page < Pagination.step * 2 + 1) {
                Pagination.Add(1, Pagination.step * 2 + 4);
                Pagination.Last();
            }
            else if (Pagination.page > Pagination.size - Pagination.step * 2) {
                Pagination.First();
                Pagination.Add(Pagination.size - Pagination.step * 2 - 2, Pagination.size + 1);
            }
            else {
                Pagination.First();
                Pagination.Add(Pagination.page - Pagination.step, Pagination.page + Pagination.step + 1);
                Pagination.Last();
            }
            Pagination.Finish();
        },

        Create: function (e) {
            e.innerHTML = "<a onclick='Pagination.Prev()'>&lt;</a>" +   // previous button
                "<span></span>" +                                       // pagination container
                "<a onclick='Pagination.Next()'>&gt;</a>";              // next button
            Pagination.e = e.getElementsByTagName('span')[0];
        },
        Init: function (e, data) {
            Pagination.Extend(data);
            Pagination.Create(e);
            Pagination.Start();
        }
    };

    $(function(){
        addPage();
    })

    //添加翻页
    function addPage() {
        var n = page_number(31, 10);
        if (n == 0) {
            $(".shortOrder>.order4").hide();
            $(".shortOrder .noMore").show();
        } else {
            $(".shortOrder>.order4").show();
            $(".shortOrder .noMore").hide();
        }
        if (n > 0) {
            var init = function () {
                Pagination.Init(document.getElementById('pagination'), {
                    size: n,
                    page: 1,
                    step: 1
                });
            };
            init();
            N = 1;
        }
    }
